    if ($(this).hasClass('openedmenu')) {
      $('.side__list').removeClass('openedmenu');
      $(this).removeClass("openedmenu");
    }else{
      $('.side__list').removeClass('openedmenu');
      $(this).addClass("openedmenu");
    }
