$('.master_field').find('input, textarea').not('input[type=file] , input.datepicker , input.timepicker').each(function () {
    var $input = $(this);

    $input.on('keyup', function () {
      if ($input.val().charCodeAt(0) > 1500) {
        $input.css({ direction: 'rtl', textAlign: 'right' });
      }else {
        $input.css({ direction: 'ltr', textAlign: 'left' });
      }
    });

    if ($input.prop('required')) {
      $input.prev('label.master_label').prepend('<span class ="mandatory"></span>');
      $('.master_input').parents('form').one().prepend('<span class ="form-mandatory">all inputs included [<span class ="mandatory"></span>] icon are mandatory</span>');
      $('.form-mandatory').not(':first').remove();

      $input.blur(function () {
        if (!$(this).val()) {
          $input.addClass('empty-input');
          $input.prev('label').addClass('label-for-required');
          $input.after('<span class ="required-label">required</span>');
        }
      });

      $input.focus(function () {
        $input.removeClass('empty-input');
        $input.prev('label').removeClass('label-for-required');
        $input.parents('.master_field').find('.required-label').remove();

      });
    }
  });

