function validateEmail(email) {
  var emailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/;
  return emailReg.test(email);
}

var $Mailinput = $('.master_field').find('input[type=email]');

$Mailinput.focus(function () {
  var $userinput = $(this).val();

  if ($userinput) {
    if (validateEmail($userinput)) {
      $(this).next('span.valid-label').css('display', 'none').html('');
    }else {
      $(this).next('span.valid-label').css('display', 'inline-block').html('invalid');
    }
  } else {
    $(this).next('span.valid-label').hide();
  }
});

$Mailinput.blur(function () {
  var $userinput = $(this).val();
  if ($(this).val()) {
    if (validateEmail($userinput)) {
      $(this).next('span.valid-label').css('display', 'none').html('');

    }else {
      $(this).next('span.valid-label').css('display', 'inline-block').html('invalid');
    }
  } else {
    $(this).next('span.valid-label').hide();
  }
});

